let Home="";let AboutUs="";let Contact="";const fetchComponents=async()=>{Home={template:await(await fetch("views/index.html")).text()};AboutUs={template:await(await fetch("views/about.html")).text()};Contact={template:await(await fetch("views/contact.html")).text()};};fetchComponents().then(()=>{const routes=[{path:'/',component:Home},{path:'/about',component:AboutUs},{path:'/contact',component:Contact}];const router=new VueRouter({routes});const app=new Vue({router}).$mount('#app');});function active(elem){$(".active").removeClass("active");element=$("#"+elem+"Link");element.addClass("active");}$(document).ready(function(){active("home");});